import React from 'react';
import './App.css';
import {Component} from 'react-simplified';

export class Terminal extends Component{

    addText(code){
        let textBar = document.querySelector(".text");
        let input = code.key();
        textBar.innerHTML = input;
    }
    render(){
        window.onkeypress = a =>  this.addText(a.code);
        return(
            <div className="container">
            <div className="root">
                ~root $
            </div>
            <div className="text">
            </div>
            </div>

        )
    }
}
